import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './IncluirChamado.css';

const IncluirChamado = () => {
    const [titulo, setTitulo] = useState('');
    const [descricao, setDescricao] = useState('');
    const [status, setStatus] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleIncluirChamado = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');

        if (!token) {
            setError('Usuário não autenticado');
            return;
        }

        try {
            const response = await axios.post('http://localhost:5216/api/chamados', {
                titulo: titulo,
                descricao: descricao,
                status: status
            }, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });

            navigate('/consulta-chamados');
        } catch (error) {
            setError('Erro ao incluir o chamado');
            console.error('Erro ao incluir o chamado:', error);
        }
    };

    return (
        <div className="incluir-chamado-container">
            <h2>Incluir Chamado</h2>
            <form onSubmit={handleIncluirChamado} className="incluir-chamado-form">
                <label>Título:</label>
                <input type="text" value={titulo} onChange={(e) => setTitulo(e.target.value)} required />
                <label>Descrição:</label>
                <textarea value={descricao} onChange={(e) => setDescricao(e.target.value)} required />
                <label>Status:</label>
                <input type="text" value={status} onChange={(e) => setStatus(e.target.value)} required />
                <button type="submit">Incluir Chamado</button>
            </form>
            {error && <p className="error-message">{error}</p>}
        </div>
    );
};

export default IncluirChamado;
