import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ConsultaChamados.css';

const ConsultaChamados = () => {
    const [chamados, setChamados] = useState([]);

    useEffect(() => {
        const carregarChamados = async () => {
            try {
                const response = await axios.get('http://localhost:5216/api/chamados');
                setChamados(response.data);
            } catch (error) {
                console.error('Erro ao carregar os chamados:', error);
            }
        };

        carregarChamados();
    }, []);

    const handleNovoChamado = () => {
        window.location.href = '/incluir-chamado';
    };

    return (
        <div className="consulta-chamados-container">
            <h2>Lista de Chamados</h2>
            <button className="novo-chamado-button" onClick={handleNovoChamado}>Novo Chamado</button>
            <ul className="chamados-list">
                {chamados.map(chamado => (
                    <li key={chamado.id} className="chamado-item">
                        <strong>{chamado.titulo}</strong>: {chamado.descricao}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ConsultaChamados;
