import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ConsultaChamados = () => {
    const [chamados, setChamados] = useState([]);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const carregarChamados = async () => {
            try {
                const token = localStorage.getItem('token'); // Obtém o token do localStorage
                const response = await axios.get('http://localhost:5216/api/chamados', {
                    headers: {
                        Authorization: `Bearer ${token}` // Envia o token no cabeçalho
                    }
                });
                setChamados(response.data); // Assumindo que a API retorna uma lista de chamados
            } catch (error) {
                console.error('Erro ao carregar os chamados:', error);
                setError('Erro ao carregar os chamados');
            }
        };

        carregarChamados();
    }, []); // Executa apenas uma vez ao carregar o componente

    const handleAdicionarChamado = () => {
        navigate('/incluir-chamado');
    };

    return (
        <div>
            <h2>Lista de Chamados</h2>
            {error && <p>{error}</p>}
            <ul>
                {chamados.map(chamado => (
                    <li key={chamado.id}>
                        <strong>{chamado.titulo}</strong>: {chamado.descricao}
                    </li>
                ))}
            </ul>
            <button onClick={handleAdicionarChamado}>Incluir Chamado</button>
        </div>
    );
};

export default ConsultaChamados;
