import React, { useState } from 'react';
import axios from 'axios';

const ConsultaChamadoEspecifico = () => {
    const [id, setId] = useState('');
    const [chamado, setChamado] = useState(null);
    const [error, setError] = useState('');

    const handleConsultaChamado = async (e) => {
        e.preventDefault();

        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`http://localhost:5216/api/chamados/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });

            setChamado(response.data);
            setError('');
        } catch (error) {
            setError('Erro ao consultar o chamado ou chamado não encontrado');
            setChamado(null);
            console.error('Erro ao consultar o chamado:', error);
        }
    };

    return (
        <div>
            <h2>Consultar Chamado Específico</h2>
            <form onSubmit={handleConsultaChamado}>
                <label>ID do Chamado:</label>
                <input type="text" value={id} onChange={(e) => setId(e.target.value)} required />
                <br />
                <button type="submit">Consultar</button>
            </form>
            {error && <p>{error}</p>}
            {chamado && (
                <div>
                    <h3>Detalhes do Chamado</h3>
                    <p><strong>ID:</strong> {chamado.id}</p>
                    <p><strong>Título:</strong> {chamado.titulo}</p>
                    <p><strong>Descrição:</strong> {chamado.descricao}</p>
                    <p><strong>Status:</strong> {chamado.status}</p>
                </div>
            )}
        </div>
    );
};

export default ConsultaChamadoEspecifico;
