import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const history = useHistory(); // Hook para histórico de navegação

    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:5216/api/autenticacao/login', {
                email: email,
                senha: senha
            });

            console.log('Token:', response.data.token);

            // Redirecionar para a tela de consulta de chamados após o login bem-sucedido
            history.push('/consulta-chamados');
        } catch (error) {
            setError('Email ou senha inválidos');
            console.error('Erro de login:', error);
        }
    };

    return (
        <div>
            <h2>Login</h2>
            <form onSubmit={handleLogin}>
                <label>Email:</label>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                <br />
                <label>Senha:</label>
                <input type="password" value={senha} onChange={(e) => setSenha(e.target.value)} required />
                <br />
                <button type="submit" disabled={loading}> {/* Desativa o botão durante o loading */}
                    {loading ? 'Carregando...' : 'Login'}
                </button>
            </form>
            {error && <p>{error}</p>}
        </div>
    );
};

export default Login;
