import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './ConsultaChamados.css';

const ConsultaChamados = () => {
    const [chamados, setChamados] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        // Função para carregar os chamados após o login
        const carregarChamados = async () => {
            const token = localStorage.getItem('token');

            if (!token) {
                console.error('Usuário não autenticado');
                navigate('/login');
                return;
            }

            try {
                const response = await axios.get('http://localhost:5216/api/chamados', {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setChamados(response.data); // Assumindo que a API retorna uma lista de chamados
            } catch (error) {
                console.error('Erro ao carregar os chamados:', error);
            }
        };

        carregarChamados();
    }, [navigate]); // Executa apenas uma vez ao carregar o componente

    return (
        <div className="consulta-chamados-container">
            <h2>Lista de Chamados</h2>
            <ul>
                {chamados.map(chamado => (
                    <li key={chamado.id}>
                        <strong>{chamado.titulo}</strong>: {chamado.descricao} - {chamado.status}
                    </li>
                ))}
            </ul>
            <button onClick={() => navigate('/incluir-chamado')}>Incluir Chamado</button><br></br>
            <button onClick={() => navigate('/consulta-chamado-especifico')}>Consultar chamado</button>
        </div>
    );
};

export default ConsultaChamados;
