import React, { useState } from 'react';
import axios from 'axios';

function IncluiChamado({ history }) {
  const [descricao, setDescricao] = useState('');
  const [status, setStatus] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5216/api/chamados', {
        descricao,
        status,
        usuarioId: 1 // Exemplo: assumindo que o usuário logado tem id 1
      }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      history.push('/chamados');
    } catch (error) {
      console.error('Erro ao incluir chamado:', error);
    }
  };

  return (
    <div>
      <h2>Incluir Chamado</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Descrição:</label>
          <textarea value={descricao} onChange={(e) => setDescricao(e.target.value)} required />
        </div>
        <div>
          <label>Status:</label>
          <select value={status} onChange={(e) => setStatus(e.target.value)} required>
            <option value="">Selecione...</option>
            <option value="Em aberto">Em aberto</option>
            <option value="Concluído">Concluído</option>
            <option value="Cancelado">Cancelado</option>
          </select>
        </div>
        <button type="submit">Salvar</button>
      </form>
    </div>
  );
}

export default IncluiChamado;
