import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ConsultarChamado({ match }) {
  const [chamado, setChamado] = useState(null);

  useEffect(() => {
    const fetchChamado = async () => {
      const result = await axios.get(`http://localhost:5000/api/chamados/${match.params.id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      setChamado(result.data);
    };
    fetchChamado();
  }, [match.params.id]);

  if (!chamado) {
    return <div>Carregando...</div>;
  }

  return (
    <div>
      <h2>Detalhes do Chamado</h2>
      <p><strong>ID:</strong> {chamado.id}</p>
      <p><strong>Data de Criação:</strong> {new Date(chamado.dataCriacao).toLocaleString()}</p>
      <p><strong>Descrição:</strong> {chamado.descricao}</p>
      <p><strong>Status:</strong> {chamado.status}</p>
    </div>
  );
}

export default ConsultarChamado;
