import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false); // Novo estado de loading

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true); // Inicia o loading ao enviar o formulário

        try {
            const response = await axios.post('http://localhost:5216/api/autenticacao/login', {
                email: email,
                senha: senha
            });

            console.log('Token:', response.data.token);
            // Aqui você pode redirecionar o usuário ou realizar outras ações após o login
        } catch (error) {
            setError('Email ou senha inválidos');
            console.error('Erro de login:', error);
        } finally {
            setLoading(false); // Finaliza o loading após a requisição
        }
    };

    return (
        <div>
            <h2>Login</h2>
            <form onSubmit={handleLogin}>
                <label>Email:</label>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                <br />
                <label>Senha:</label>
                <input type="password" value={senha} onChange={(e) => setSenha(e.target.value)} required />
                <br />
                <button type="submit" disabled={loading}> {/* Desativa o botão durante o loading */}
                    {loading ? 'Carregando...' : 'Login'}
                </button>
            </form>
            {error && <p>{error}</p>}
        </div>
    );
};

export default Login;
