import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ConsultaChamados = () => {
    const [chamados, setChamados] = useState([]);

    useEffect(() => {
        // Função para carregar os chamados após o login
        const carregarChamados = async () => {
            try {
                const response = await axios.get('http://localhost:5216/api/chamados');
                setChamados(response.data); // Assumindo que a API retorna uma lista de chamados
            } catch (error) {
                console.error('Erro ao carregar os chamados:', error);
            }
        };

        carregarChamados();
    }, []); // Executa apenas uma vez ao carregar o componente

    return (
        <div>
            <h2>Lista de Chamados</h2>
            <ul>
                {chamados.map(chamado => (
                    <li key={chamado.id}>
                        <strong>{chamado.titulo}</strong>: {chamado.descricao}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ConsultaChamados;
