import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './IncluirChamado.css';

const IncluirChamado = () => {
    const [titulo, setTitulo] = useState('');
    const [descricao, setDescricao] = useState('');
    const [status, setStatus] = useState('em aberto');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleIncluirChamado = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');

        if (!token) {
            console.error('Usuário não autenticado');
            navigate('/login');
            return;
        }

        try {
            await axios.post('http://localhost:5216/api/chamados', {
                titulo: titulo,
                descricao: descricao,
                status: status
            }, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            navigate('/consulta-chamados'); // Redireciona para a tela de consulta de chamados após a inclusão
        } catch (error) {
            setError('Erro ao incluir o chamado');
            console.error('Erro ao incluir o chamado:', error);
        }
    };

    return (
        <div className="incluir-chamado-container">
            <h2>Incluir Chamado</h2>
            <form onSubmit={handleIncluirChamado}>
                <label>Título:</label>
                <input
                    type="text"
                    value={titulo}
                    onChange={(e) => setTitulo(e.target.value)}
                    required
                />
                <br />
                <label>Descrição:</label>
                <textarea
                    value={descricao}
                    onChange={(e) => setDescricao(e.target.value)}
                    required
                />
                <br />
                <label>Status:</label>
                <select
                    value={status}
                    onChange={(e) => setStatus(e.target.value)}
                    required
                >
                    <option value="em aberto">Em aberto</option>
                    <option value="concluído">Concluído</option>
                    <option value="cancelado">Cancelado</option>
                </select>
                <br />
                <button type="submit">Incluir</button>
            </form>
            {error && <p>{error}</p>}
        </div>
    );
};

export default IncluirChamado;
